definition.script:
properties(
    [
        [$class: 'RebuildSettings', 
        autoRebuild: false, 
        rebuildDisabled: false], 
        buildDiscarder(
            logRotator(
                artifactDaysToKeepStr: '', 
                artifactNumToKeepStr: '', 
                daysToKeepStr: '', 
                numToKeepStr: '24')), 
        parameters([string(defaultValue: '10', name: 'THREAD'), 
        string(
            defaultValue: '10', 
            name: 'LOOP'), 
        string(
            defaultValue: '600', 
            name: 'DURATION'), 
        choice(
            choices: ['Geology.jmx'], 
            name: 'SCENARIO')
    ])
])

node{
    stage("delete dir"){
        deleteDir()
    }
    stage("git download"){
        git branch: 'main', url: 'https://github.com/youketero/jmeter_test'
    }
    stage("run jmeter test"){
        Date startDate = new Date()
        echoStartTest(startDate)
        startDateString = date(startDate)
        def sTimestamp = System.currentTimeMillis().toString()
        sh 'docker run --name jmeter --rm --volumes-from=jmeter-elastic-jenkins -e SCENARIO=$WORKSPACE/$SCENARIO -e WORKSPACE=$WORKSPACE -e PARAMETERS=" -JTHREADS=$THREAD -JLOOP=$LOOP " -e BUILD_NUMBER=$BUILD_NUMBER java:8-jdk '

        Date endDate = new Date();
        echo endDate.toString()
        echoEndTest(endDate)
        endDateString = date(endDate)

         //get runtime 
		String runtime = getDuration(endDate,startDate)
        echo "Test run time: " + runtime
        def eTimestamp = System.currentTimeMillis().toString()
        // Kibana links	
        echo "Kibana Dashboard URL: \n" + getKibanaDashboardUrl(startDateString, endDateString)
        echo "Kibana Metric Beat URL: \n" + getKibanaMetricBeatUrl()
    }
    stage("archive artifacts"){
        publishHTML([
            keepAll: false, 
            reportDir: 'report_$BUILD_NUMBER', 
            reportFiles: 'index.html', 
            reportName: 'Report'
            ])
        archiveArtifacts artifacts: "jmeter_${BUILD_NUMBER}.log, report_${BUILD_NUMBER}.csv, report_${BUILD_NUMBER}/**', followSymlinks: false"
    }
}


def getKibanaDashboardUrl(startDateString, endDateString){
    start = URLEncoder.encode(startDateString,"UTF-8")
    end = URLEncoder.encode(endDateString,"UTF-8")
    return "http://localhost:5601/app/kibana#/dashboard/d5324020-98a8-11eb-aafc-033b3d458859?_g=(filters:!(),refreshInterval:(pause:!t,value:0),time:(from:'${start}',to:'${end}'))"
}

def getKibanaMetricBeatUrl(){
    return "http://localhost:5601/app/infra#/infrastructure/inventory"
}

def date(time){
    return time.format("yyyy-MM-dd'T'HH:mm:ss.SSSZ").toString()
}

def echoStartTest(time){
    echo "Start JMeter test: " + time.toString()
}

def echoEndTest(time){
    echo "End JMeter test: " + time.toString()
}

String getDuration(Date endDate,Date startDate){
	return groovy.time.TimeCategory.minus(endDate, startDate).toString()
}
